// Extract and validate job_id from query parameters
const query = $input.first().json.query;

if (!query || !query.job_id) {
  return [{
    json: {
      error: true,
      message: "Missing required parameter: job_id",
      code: "MISSING_JOB_ID"
    }
  }];
}

return [{
  json: {
    job_id: query.job_id,
    error: false
  }
}];

