parameters.jsCode:
// Prepare comprehensive status response
const jobData = $node["ğŸ” Lookup Job"].json;
const scenes = $input.all().map(item => item.json);

// Calculate progress
const totalScenes = scenes.length;
const completedScenes = scenes.filter(s => s.video_status === 'completed').length;
const failedScenes = scenes.filter(s => s.video_status === 'failed').length;
const processingScenes = totalScenes - completedScenes - failedScenes;

const progressPercent = totalScenes > 0 ? Math.round((completedScenes / totalScenes) * 100) : 0;

// Determine overall stage
let currentStage = jobData.stage || 'initialized';
let estimatedCompletion = null;

// Estimate time remaining
if (jobData.status === 'processing') {
  const stages = {
    'initialized': { progress: 5, eta: 480 },
    'prompts_generated': { progress: 15, eta: 420 },
    'images_created': { progress: 30, eta: 360 },
    'scenes_completed': { progress: 70, eta: 180 },
    'final_assets_ready': { progress: 95, eta: 30 }
  };

  const stageInfo = stages[currentStage] || { progress: 50, eta: 240 };
  estimatedCompletion = `${Math.floor(stageInfo.eta / 60)} minutes`;
}

// Build response
const response = {
  success: true,
  job_id: jobData.job_id,
  project_name: jobData.project_name,
  status: jobData.status,
  stage: currentStage,
  progress_percent: progressPercent,
  estimated_completion: estimatedCompletion,
  created_at: jobData.created_at,
  updated_at: jobData.updated_at,
  scenes: {
    total: totalScenes,
    completed: completedScenes,
    processing: processingScenes,
    failed: failedScenes
  }
};

// Add final URLs if completed
if (jobData.status === 'completed') {
  response.results = {
    music_url: jobData.music_url,
    voice_url: jobData.voice_url,
    video_urls: scenes.map(s => s.video_url).filter(Boolean),
    completed_at: jobData.completed_at
  };
}

// Add error info if failed
if (jobData.status === 'failed') {
  response.error_message = jobData.error_message;
}

return [{ json: response }];
